// 입력 데이터 안전하게 추출
const inputData = $input.item.json;
const body = inputData.body || inputData;

// 로그 출력 (디버깅용)
console.log('받은 데이터:', JSON.stringify(body, null, 2));

// 필수 필드 확인
if (!body.name) {
  return { error: true, message: '이름이 필요합니다' };
}
if (!body.email) {
  return { error: true, message: '이메일이 필요합니다' };
}

// 성공 응답 데이터 생성
return {
  success: true,
  message: '구독 신청이 완료되었습니다',
  data: {
    name: body.name,
    email: body.email,
    phone: body.phone || '',
    receivedAt: new Date().toISOString()
  }
};
